import React from "react";

import {
    Text,
    TopHr,
    BotHr
} from "./UserInfo.style";

import {
    Row,
    Col
} from "react-bootstrap";

import {useFetchProjects} from "../../Hooks/useFetchProjects";
import Buttom from "../buttom/index";


const Projects = () => {

    const {
        state,
        error,
        setLoadMore,
        loadMore
    } = useFetchProjects();

    console.log(state)
    if (error) return <div> something went roung </div>

    return (

        <Col md={12} className="">
            <Text>
                <h1 className="user-info-title" style={{fontSize: "1.6em"}}> Projects </h1>
                <TopHr/>
                <Row>
                    {state.projects.map(
                        project => (
                            <>
                                <Col md={1}></Col>
                                <Col md={11}>
                                    <p className="user-info-body">&#9677;
                                        <b>{project.title}</b> ,
                                        {project.long_title}
                                    </p>
                                </Col>

                                <Col md={2}></Col>
                                <Col md={10}>
                                    <p className="user-info-body">&#9675;
                                        {project.description}
                                    </p>
                                </Col>
                            </>
                        )
                    )
                    }
                    <Col md={12}>
                        <Buttom text={loadMore ? 'show less' : 'show more'} callback={() => setLoadMore(true)}/>
                    </Col>
                </Row>
            </Text>
        </Col>
    );
}

export default Projects;
