import {useEffect, useState} from "react";


const initialState = {
    projects: [],
}

export const useFetchProjects = () => {

    const [state, setState] = useState(initialState);
    const [error, setError] = useState(false)

    const fetchMovie = () => {
        try {
            const fs = require("fs")
            fs.readFile('../Assets/texts/project_dis.JSON', 'utf8', function(err, data) {
                if(err) throw err;
                const obj = JSON.parse(data);
                console.log(obj);
            });
            setError(false);
           // setState(project);
        } catch (error) {
            setError(true);
        }
    };

    useEffect(() => {
        fetchMovie()
    }, [])

    return {state, error};

}
