import {useEffect, useState} from "react";


const initialState = {
    projects: [],
}

export const useFetchProjects = () => {

    const [state, setState] = useState(initialState);
    const [error, setError] = useState(false)

    const fetchMovie = () => {
        try {
            setError(false);
            console.log('here');
            fetch("./src/Assets/texts/project_dis.json", {
                headers : {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }).then(
                function(res){
                    return res.json()
                }).then(function(data){
                // store Data in State Data Variable
                console.log(data);
                setState(data)
            }).catch(
                function(err){
                    console.log(err, ' error')
                }
            )
            console.log(state);
            // setState(project);
        } catch (error) {
            setError(true);
        }
    };

    useEffect(() => {
        fetchMovie()
    }, [])

    return {state, error};

}
