import {useEffect, useState} from "react";


const initialState = {
    projects: [],
}

export const useFetchProjects = () => {

    const [state, setState] = useState(initialState);
    const [error, setError] = useState(false)

    const fetchMovie = () => {
        try {
            setError(false);
            console.log('here');
            var data = require('json!../Assets/texts/project_dis.JSON');

            console.log(data);
           // setState(project);
        } catch (error) {
            setError(true);
        }
    };

    useEffect(() => {
        fetchMovie()
    }, [])

    return {state, error};

}
