import {useEffect, useState} from "react";
import greetings from "./project_dis.json";


const initialState = {
    projects: [],
}

export const useFetchProjects = () => {

    const [state, setState] = useState(initialState);
    const [loadMore, setLoadMore] = useState(false);
    const [error, setError] = useState(false)

    const fetchMovie = () => {
        try {
            setError(false);
            console.log('here');
            const greetings = require('./project_dis.json')
            if (loadMore) {
                setState(greetings.projects.slice(0, MAX_ITEMS));

            }
            return this.items.slice(0, MAX_ITEMS);
            console.log(greetings);
            // setState(project);
        } catch (error) {
            setError(true);
        }
    };

    useEffect(() => {
        fetchMovie()
    }, [])

    return {state, error};

}
